CREATE TABLE "MATERIAL_GROUP" (
	"ID"			VARCHAR2(20 BYTE) NOT NULL,
	"NAME"			VARCHAR2(50 BYTE) NOT NULL,
	"UPDATED_ON"	DATE,
	"CREATED_ON"	DATE,
	"UPDATED_BY"	VARCHAR2(16 BYTE),
	"CREATED_BY"	VARCHAR2(16 BYTE),
	"VERSION"		NUMBER(3,0),
	CONSTRAINT "MATERIAL_GROUP_PK" PRIMARY KEY ("ID") USING INDEX
);

CREATE TABLE "MATERIAL" (
	"ID"				NUMBER(10,0) NOT NULL,
	"NAME"				VARCHAR2(50 BYTE) NOT NULL,
	"MATERIAL_GROUP"	VARCHAR2(20 BYTE) NULL,
	"UPDATED_ON"		TIMESTAMP,
	"CREATED_ON"		TIMESTAMP,
	"UPDATED_BY"		VARCHAR2(16 BYTE),
	"CREATED_BY"		VARCHAR2(16 BYTE),
	"VERSION"			NUMBER(3,0),
	CONSTRAINT "MATERIAL_PK" PRIMARY KEY ("ID") USING INDEX,
	CONSTRAINT "MATERIAL_MATERIAL_GROUP_FK1" FOREIGN KEY ("MATERIAL_GROUP") REFERENCES "MATERIAL_GROUP" ("ID") DEFERRABLE INITIALLY DEFERRED
);
CREATE SEQUENCE "MATERIAL_SEQ"  MINVALUE 1 MAXVALUE 2000000000 INCREMENT BY 1 START WITH 10000 CACHE 20 NOORDER NOCYCLE ;

CREATE TABLE "UNIT" (
	"ID"   NUMBER(18,0) NOT NULL,
	"NAME" VARCHAR2(20 BYTE) NOT NULL,
	"UPDATED_ON" DATE,
	"CREATED_ON" DATE,
	"UPDATED_BY" VARCHAR2(16 BYTE),
	"CREATED_BY" VARCHAR2(16 BYTE),
	"VERSION"    NUMBER(9,0),
	CONSTRAINT "UNIT_PK" PRIMARY KEY ("ID") USING INDEX
);
CREATE SEQUENCE "UNIT_SEQ"  MINVALUE 1 MAXVALUE 2000000000 INCREMENT BY 1 START WITH 100 CACHE 20 NOORDER NOCYCLE ;

CREATE TABLE "LINK_MATERIAL_UNIT" (
	"LEFT_ID"  NUMBER NOT NULL,
	"RIGHT_ID" NUMBER NOT NULL,
	CONSTRAINT "LINK_MATERIAL_UNIT_PK" PRIMARY KEY ("LEFT_ID", "RIGHT_ID") USING INDEX,
	CONSTRAINT "LINK_MATERIAL_UNIT_MATERI_FK1" FOREIGN KEY ("LEFT_ID") REFERENCES "MATERIAL" ("ID") DEFERRABLE INITIALLY IMMEDIATE,
	CONSTRAINT "LINK_MATERIAL_UNIT_UNIT_FK1" FOREIGN KEY ("RIGHT_ID") REFERENCES "UNIT" ("ID") DEFERRABLE INITIALLY IMMEDIATE
);
