package de.osthus.ambeth.persistence.jdbc.compositeid;import static org.junit.Assert.assertEquals;import static org.junit.Assert.assertFalse;import static org.junit.Assert.assertNotNull;import static org.junit.Assert.assertNull;import static org.junit.Assert.assertTrue;import java.util.List;import org.junit.Test;import de.osthus.ambeth.cache.ICache;import de.osthus.ambeth.config.ServiceConfigurationConstants;import de.osthus.ambeth.ioc.annotation.Autowired;import de.osthus.ambeth.persistence.jdbc.compositeid.models.CompositeIdEntity;import de.osthus.ambeth.persistence.jdbc.compositeid.models.ICompositeIdEntityService;import de.osthus.ambeth.persistence.jdbc.config.PersistenceJdbcConfigurationConstants;import de.osthus.ambeth.query.IQuery;import de.osthus.ambeth.query.IQueryBuilder;import de.osthus.ambeth.query.IQueryBuilderFactory;import de.osthus.ambeth.testutil.AbstractPersistenceTest;import de.osthus.ambeth.testutil.SQLData;import de.osthus.ambeth.testutil.SQLStructure;import de.osthus.ambeth.testutil.TestModule;import de.osthus.ambeth.testutil.TestProperties;import de.osthus.ambeth.testutil.TestPropertiesList;@TestModule({ CompositeIdTestModule.class })@TestPropertiesList({ @TestProperties(name = PersistenceJdbcConfigurationConstants.DatabaseBehaviourStrict, value = "true"),		@TestProperties(name = ServiceConfigurationConstants.mappingFile, value = "de/osthus/ambeth/persistence/jdbc/compositeid/orm.xml") })@SQLStructure("CompositeId_structure.sql")@SQLData("CompositeId_data.sql")public class CompositeIdTest extends AbstractPersistenceTest{	@Autowired	private ICache cache;	@Autowired	private ICompositeIdEntityService compositeIdEntityService;	@Autowired	private IQueryBuilderFactory qbf;	@Test	public void testCreate() throws Exception	{		CompositeIdEntity entity = entityFactory.createEntity(CompositeIdEntity.class);		entity.setName("zehn tausend");		compositeIdEntityService.create(entity);	}	@Test	public void testUpdate() throws Exception	{		String newName = "eins punkt zwei";		CompositeIdEntity entity = cache.getObject(CompositeIdEntity.class, 1);		assertFalse(newName.equals(entity.getName()));		entity.setName(newName);		compositeIdEntityService.update(entity);		CompositeIdEntity actual = cache.getObject(CompositeIdEntity.class, 1);		assertTrue(newName.equals(actual.getName()));	}	@Test	public void testDelete() throws Exception	{		CompositeIdEntity entity = cache.getObject(CompositeIdEntity.class, 1);		assertNotNull(entity);		compositeIdEntityService.delete(entity);		CompositeIdEntity actual = cache.getObject(CompositeIdEntity.class, 1);		assertNull(actual);	}	@Test	public void testQuery() throws Exception	{		IQueryBuilder<CompositeIdEntity> qb = qbf.create(CompositeIdEntity.class);		IQuery<CompositeIdEntity> query = qb.build(qb.isEqualTo(qb.property("Name"), qb.value("name_einszwo1")));		List<CompositeIdEntity> result = query.retrieve();		assertEquals(1, result.size());		CompositeIdEntity actual = result.get(0);		assertEquals(1, actual.getId1());		assertEquals("einszwo", actual.getId2());		assertEquals("name_einszwo1", actual.getName());	}}