package de.osthus.ambeth.persistence.schema;import static org.junit.Assert.assertEquals;import static org.junit.Assert.assertNotNull;import static org.junit.Assert.assertNull;import java.util.List;import org.junit.Ignore;import org.junit.Test;import de.osthus.ambeth.cache.ICache;import de.osthus.ambeth.config.ServiceConfigurationConstants;import de.osthus.ambeth.ioc.annotation.Autowired;import de.osthus.ambeth.persistence.jdbc.config.PersistenceJdbcConfigurationConstants;import de.osthus.ambeth.persistence.schema.models.IParentAService;import de.osthus.ambeth.persistence.schema.models.IParentBService;import de.osthus.ambeth.persistence.schema.models.ParentA;import de.osthus.ambeth.query.IQuery;import de.osthus.ambeth.query.IQueryBuilder;import de.osthus.ambeth.query.IQueryBuilderFactory;import de.osthus.ambeth.testutil.AbstractInformationBusWithPersistenceTest;import de.osthus.ambeth.testutil.SQLData;import de.osthus.ambeth.testutil.SQLStructure;import de.osthus.ambeth.testutil.TestModule;import de.osthus.ambeth.testutil.TestProperties;import de.osthus.ambeth.testutil.TestPropertiesList;/** * Tests the replacement of the JAMBETH user by a temporary user created by the RandomUserScript. The Jenkins build script has to be adapted so that the * database connection is done with this temporary user instead of the JAMBETH one. Because of that fact we don't have to do anything special (if Jenkins is * properly configured). * <p> * This test won't run in a local test environment. */@TestModule({ MultiSchemaTestModule.class })@TestPropertiesList({ @TestProperties(file = "citemp.properties"),		@TestProperties(name = PersistenceJdbcConfigurationConstants.DatabaseBehaviourStrict, value = "true"),		@TestProperties(name = ServiceConfigurationConstants.mappingFile, value = "de/osthus/ambeth/persistence/schema/single_random_user_orm.xml") })@SQLStructure("single_random_user_structure.sql")@SQLData("single_random_user_data.sql")@Ignore("No need to test in JENKINS - all other tests using a connection will do the same")public class JenkinsSingleRandomUserTest extends AbstractInformationBusWithPersistenceTest{	@Autowired	private ICache cache;	@Autowired	private IQueryBuilderFactory qbf;	@Autowired	private IParentAService parentAService;	@Autowired	private IParentBService parentBService;	@Test	public void testDelete() throws Exception	{		ParentA parent = cache.getObject(ParentA.class, 1);		assertNotNull(parent);		parentAService.delete(parent);		ParentA actual = cache.getObject(ParentA.class, 1);		assertNull(actual);	}	@Test	public void testQuery() throws Exception	{		IQueryBuilder<ParentA> qb = qbf.create(ParentA.class);		IQuery<ParentA> query = qb.build(qb.isEqualTo(qb.property("Child.Id"), qb.value(11)));		List<ParentA> result = query.retrieve();		assertEquals(1, result.size());		ParentA actual = result.get(0);		assertEquals(1, actual.getId());		assertEquals(11, actual.getChild().getId());	}}